import React, { useState, useEffect } from 'react';
import './App.css';
import { MapContainer, TileLayer, Marker, Circle } from 'react-leaflet';
import issIcon from '../components/issIcon';

/*const getData = async() => {
          await fetch("http://api.open-notify.org/iss-now.json")
          await resp.json(resp)
          var lat = Response['iss_position']['latitude'];
          var lon = Response['iss_position']['longitude'];
          console.log(lat,lon);

          map.setView([lat,lon])
          marker.setLatLng([lat,lon]);
          circle.setLatLng([lat,lon]);
          setInterval(getData(), 5000);
      } 
      getData();*/

function App() {
  const [longitude, setLongitude] = useState(0);
  const [latitude, setLatitude] = useState(0);
  console.log(longitude,latitude);

  useEffect(() => {
    getLocation()
}, []);

const getLocation = async () => {
  //await fetch("http://api.open-notify.org/iss-now.json")
  const resp = fetch("http://api.open-notify.org/iss-now.json")
    .then((response) => response.json())
         .then((data) => {
            setLongitude(data.iss_position.longitude);
            setLatitude(data.iss_position.latitude);
    })
  }

  return (
      <header className="ISS Tracker">
        <MapContainer center={[51.505, -0.09]} zoom={6} scrollWheelZoom={false}>
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <Circle center={[51.505, -0.09]} radius={80000} color={'red'} opacity={0.3}/>
          <Marker position={[51.505, -0.09]} icon={issIcon()}>
          </Marker>
        </MapContainer>

      </header>
  );
}

export default App;
