import React, { useState, useEffect } from 'react';
import './App.css';
import { MapContainer, TileLayer, Marker, Circle, useMap } from 'react-leaflet';
import issIcon from '../components/issIcon';

/*const getData = async() => {
          await fetch("http://api.open-notify.org/iss-now.json")
          await resp.json(resp)
          var lat = Response['iss_position']['latitude'];
          var lon = Response['iss_position']['longitude'];
          console.log(lat,lon);

          map.setView([lat,lon])
          marker.setLatLng([lat,lon]);
          circle.setLatLng([lat,lon]);
          setInterval(getData(), 5000);
      } 
      getData();*/

function App() {
  const [longitude, setLongitude] = useState(-0.09);
  const [latitude, setLatitude] = useState(51.505);
  //console.log(longitude,latitude);

  useEffect(() => {
    setInterval(() => {
      getLocation()
    }, 5000);
  }, []);

  const getLocation = async () => {
    await fetch("http://api.open-notify.org/iss-now.json")
      .then((response) => response.json())
          .then((data) => {
              setLongitude(data.iss_position.longitude);
              setLatitude(data.iss_position.latitude);
      })
    }

  function MyComponent() {
    const map = useMap('click', () => {
    map.setCenter([50.5, 30.5])
  })
  return null
}

  /*function centerMap() {
    const map = useMap()
    console.log('map center:', map.getCenter())
  }*/

  return (
      <header className="ISS Tracker">
        <MapContainer center={{lng: longitude, lat: latitude}} zoom={6} scrollWheelZoom={true}>
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <Circle center={{lng:longitude, lat:latitude}} radius={80000} color={'red'} opacity={0.3}/>
          <Marker position={{lng:longitude, lat:latitude}} icon={issIcon()}/>
          <MyComponent />
        </MapContainer>

      </header>
  );
}

export default App;
